mapper.insert:
insert into t_comment (id, articleid, content, 
  author, ip, publishtime, 
  reviewstatus, reviewer,  reviewdate, reviewComment
  )
values (#{id,jdbcType=BIGINT}, #{articleid,jdbcType=BIGINT}, #{content,jdbcType=VARCHAR}, 
  #{author,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{publishtime,jdbcType=TIMESTAMP}, 
   #{reviewstatus,jdbcType=TINYINT}, #{reviewer,jdbcType=VARCHAR}, #{reviewdate,jdbcType=TIMESTAMP}, #{reviewcomment,jdbcType=VARCHAR}
  )

mapper.update:
UPDATE t_comment
SET reviewstatus = #{reviewstatus,jdbcType=TINYINT},
reviewer = #{reviewer,jdbcType=VARCHAR},
reviewdate = #{reviewdate,jdbcType=TIMESTAMP}

    ,reviewComment = #{reviewComment,jdbcType=VARCHAR}

WHERE
reviewstatus=0 and
id = #{id,jdbcType=BIGINT}
