mapper.insert:
insert into t_honour (id, imageid, pepole, 
  honortitle, title, author, 
  publishdate, content
  )
values (#{id,jdbcType=BIGINT}, #{imageid,jdbcType=BIGINT}, #{pepole,jdbcType=VARCHAR}, 
  #{honortitle,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, 
  #{publishdate,jdbcType=TIMESTAMP}, #{pageview,jdbcType=BIGINT}, #{content,jdbcType=LONGVARBINARY}
  )

mapper.select:
select id, imageid, pepole, honortitle, title, author, publishdate, pageview, content
from t_honour
where id = #{id,jdbcType=BIGINT}
