mapper.insert:
insert into t_quality (id, `type`, title, author, publishdate, content,pageview)
values (#{id,jdbcType=BIGINT}, #{type,jdbcType=TINYINT}, #{title,jdbcType=VARCHAR}, 
  #{author,jdbcType=VARCHAR}, #{publishdate,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARBINARY}
  ,#{pageview,jdbcType=BIGINT}
  )

mapper.select:
