mapper.select:
SELECT * from
 (SELECT 0 as articletype,COUNT(*) as count from t_news WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 1 as articletype,COUNT(*) as count from t_notice WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 2 as articletype,COUNT(*) as count from t_honour WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 3 as articletype,COUNT(*) as count from t_quality WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 4 as articletype,COUNT(*) as count from t_regulations WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 5 as articletype,COUNT(*) as count from t_orgtrain WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  UNION ALL
  SELECT 6 as articletype,COUNT(*) as count from t_knowledgeshare WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
  )tmp_article
 where count >0

SELECT 0 as articletype,id as articleid,title,summary,publishdate from t_news WHERE title LIKE #{fastsearch,jdbcType=VARCHAR}
UNION ALL
