mapper.update:
update t_sys_role
set `role` = #{role,jdbcType=VARCHAR},
  descr = #{descr,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, `role`, descr
from t_sys_role
where id = #{id,jdbcType=BIGINT}

select count(*) from T_SYS_ROLEPERMS
where (roleid=#{bindShip.roleid,jdbcType=BIGINT} and permissionid in

    #{permsid,jdbcType=BIGINT}

)

mapper.delete:
delete from T_SYS_ROLEPERMS
where (roleid=#{bindShip.roleid,jdbcType=BIGINT} and permissionid in

    #{permsid,jdbcType=BIGINT}

)

mapper.select:
select count(*) from T_SYS_ROLEPERMS
where 1=1 and roleid in (

    #{rid,jdbcType=BIGINT}

)

mapper.update:
delete from T_SYS_ROLEPERMS trp
where trp.roleid=#{modulesBind.roleid,jdbcType=NUMERIC}
and trp.permissionid in (
select tcsp.id from T_SYS_PERMISSION tcsp
