mapper.update:
update t_quality
set title = #{title,jdbcType=VARCHAR},
  author = #{author,jdbcType=VARCHAR},
  publisdate = #{publisdate,jdbcType=TIMESTAMP},
  coment = #{coment,jdbcType=LONGVARBINARY}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, title, author, publisdate, coment
from t_quality
where id = #{id,jdbcType=BIGINT}
