mapper.insert:
insert into t_regulations (id, title, author, 
  publishdate, dept, coment
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, 
  #{publishdate,jdbcType=TIMESTAMP}, #{dept,jdbcType=VARCHAR}, #{coment,jdbcType=LONGVARBINARY}
  )

mapper.select:
select id, title, author, publishdate, dept, coment
from t_regulations
where id = #{id,jdbcType=BIGINT}
